#include <stdio.h>

int main()
{
    int a, b;
    a = 45;
    b = 18;

    printf("a + b = %d\n", a + b);
    printf("a - b = %d\n", a - b);
    printf("a * b = %d\n", a * b);
    printf("a / b = %d\n", a / b);

    // %d for integers
    // %f for real values (Floating point numbers )
    // %c for characters

    return 0;
}
